mapper.delete:
update web_user
SET is_del = 1
where id = #{id,jdbcType=INTEGER}

mapper.select:
SELECT

,

FROM web_user

    1 = 1
    AND is_del = 0

        AND username LIKE #{username}


        AND realname LIKE #{realname}


        AND password = password(#{password})


ORDER BY create_date DESC
LIMIT #{startIndex},#{pageSize}

SELECT COUNT(*) FROM web_user
1 = 1 AND is_del = 0
AND username LIKE #{username}
AND realname LIKE #{realname}
AND password = password(#{password})
